import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';

import { AppComponent } from './app.component';
import { UsersComponent } from './users/users.component';
import { HeaderComponent } from './header/header.component';
import { FooterComponent } from './footer/footer.component';

import { RouterModule, Routes } from '@angular/router';
import { UserService } from './users/user.service';
import { HttpClientModule } from '@angular/common/http';
import { FormComponent } from './users/form.component'
import { FormsModule } from '@angular/forms';
import { ArticlesComponent } from './articles/articles.component';
import { ArticlesFormComponent } from './articles/articlesForm.component';
import { ArticleService } from './articles/article.service';

const routes: Routes = [
  {path: '', redirectTo: '/users', pathMatch: 'full'},
  {path: 'users', component: UsersComponent},
  {path: 'users/form', component: FormComponent},
  {path: 'users/form/:id', component: FormComponent},
  {path: 'articles', component: ArticlesComponent},
  {path: 'articles/form', component: ArticlesFormComponent},
  {path: 'articles/form/:id', component: ArticlesFormComponent},
];

@NgModule({
  declarations: [
    AppComponent,
    HeaderComponent,
    FooterComponent,
    UsersComponent,
    FormComponent,
    ArticlesComponent,
    ArticlesFormComponent
  ],
  imports: [
    BrowserModule,
    HttpClientModule,
    RouterModule.forRoot(routes),
    FormsModule
  ],
  providers: [
    UserService,
    ArticleService
  ],
  bootstrap: [AppComponent]
})
export class AppModule { }
